import React, { useState } from 'react';
import { products } from '../data/products';
import { useNavigate } from 'react-router-dom';

const categories = [
  { id: 'all', name: 'Tous les produits' },
  { id: 'cuisine', name: 'Cuisine Professionnelle' },
  { id: 'restauration', name: 'Restauration Rapide' },
  { id: 'snacking', name: 'Snacking' },
  { id: 'hotellerie', name: 'Hôtellerie' },
  { id: 'boulangerie', name: 'Boulangerie' },
  { id: 'collective', name: 'Cuisine Collective' },
  { id: 'bar', name: 'Bar' },
  { id: 'stockage', name: 'Stockage' },
  { id: 'nettoyage', name: 'Nettoyage' },
  { id: 'specialise', name: 'Spécialisé' }
];

const Products = () => {
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedCategory, setSelectedCategory] = useState('all');
  const [sortBy, setSortBy] = useState('name');
  const navigate = useNavigate();

  const featuredProducts = products.slice(0, 3);

  const filteredProducts = products
    .filter(product => {
      const matchesSearch = product.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
        product.description.toLowerCase().includes(searchTerm.toLowerCase());
      const matchesCategory = selectedCategory === 'all' ||
        product.category === selectedCategory;
      return matchesSearch && matchesCategory;
    })
    .sort((a, b) => {
      if (sortBy === 'name') {
        return a.title.localeCompare(b.title);
      }
      return 0;
    });

  return (
    <div className="py-10 px-4 max-w-7xl mx-auto bg-gradient-to-br from-[#14213d] via-[#1a223f] to-[#14213d] min-h-screen">
      <h1 className="text-center text-white mb-10 text-4xl font-bold uppercase tracking-wide">Nos Produits</h1>

      {/* Featured Products */}
      <div className="mb-16 bg-transparent p-0 rounded-2xl">
        <h2 className="text-center text-white mb-10 text-3xl font-semibold uppercase tracking-wide">Produits Vedettes</h2>
        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-8">
          {featuredProducts.map(product => (
            <div
              key={product.id}
              className="bg-[#1a223f] rounded-2xl shadow-lg border border-transparent hover:border-blue-400 hover:shadow-[0_0_20px_4px_rgba(59,130,246,0.5)] transition duration-300 overflow-hidden flex flex-col"
            >
              <img
                src={product.image}
                alt={product.title}
                className="h-56 w-full object-cover transition-transform duration-300 hover:scale-105"
              />
              <div className="p-6 flex-1 flex flex-col">
                <span className="inline-block bg-blue-900/30 text-blue-300 px-3 py-1 rounded-full text-xs font-semibold uppercase mb-3">
                  {categories.find(cat => cat.id === product.category)?.name}
                </span>
                <h3 className="text-xl font-bold text-white mb-2">{product.title}</h3>
                <p className="text-blue-100 mb-4 flex-1">{product.description}</p>
                <div className="flex flex-wrap gap-2 mb-4">
                  {product.features.map((feature, idx) => (
                    <span key={idx} className="bg-blue-800/40 text-blue-200 px-2 py-1 rounded-full text-xs border border-blue-700">{feature}</span>
                  ))}
                </div>
                <button
                  className="bg-green-500 text-white rounded-full px-5 py-2 font-semibold uppercase tracking-wide w-full transition hover:bg-green-600"
                  onClick={() => navigate(`/produit/${product.id}`)}
                >
                  En savoir plus
                </button>
              </div>
            </div>
          ))}
        </div>
      </div>

      {/* Search and Filter */}
      <div className="max-w-7xl mx-auto mb-10 p-6 bg-[#1a223f] rounded-xl flex flex-wrap gap-4 items-center shadow-lg">
        <input
          type="text"
          placeholder="Rechercher un produit..."
          value={searchTerm}
          onChange={e => setSearchTerm(e.target.value)}
          className="flex-1 min-w-[200px] px-4 py-3 border-2 border-blue-900 bg-[#14213d] text-white rounded-full text-base focus:outline-none focus:border-blue-400 focus:ring-2 focus:ring-blue-200 transition placeholder:text-blue-200"
        />
        <select
          value={selectedCategory}
          onChange={e => setSelectedCategory(e.target.value)}
          className="px-4 py-3 border-2 border-blue-900 bg-[#14213d] text-white rounded-full text-base min-w-[180px] focus:outline-none focus:border-blue-400 focus:ring-2 focus:ring-blue-200 transition"
        >
          {categories.map(category => (
            <option key={category.id} value={category.id}>{category.name}</option>
          ))}
        </select>
        <select
          value={sortBy}
          onChange={e => setSortBy(e.target.value)}
          className="px-4 py-3 border-2 border-blue-900 bg-[#14213d] text-white rounded-full text-base min-w-[160px] focus:outline-none focus:border-blue-400 focus:ring-2 focus:ring-blue-200 transition"
        >
          <option value="name">Trier par nom</option>
        </select>
      </div>

      {/* All Products */}
      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-8">
        {filteredProducts.length > 0 ? (
          filteredProducts.map(product => (
            <div
              key={product.id}
              className="bg-[#1a223f] rounded-2xl shadow-lg border border-transparent hover:border-blue-400 hover:shadow-[0_0_20px_4px_rgba(59,130,246,0.5)] transition duration-300 overflow-hidden flex flex-col"
            >
              <img
                src={product.image}
                alt={product.title}
                className="h-56 w-full object-cover transition-transform duration-300 hover:scale-105"
              />
              <div className="p-6 flex-1 flex flex-col">
                <span className="inline-block bg-blue-900/30 text-blue-300 px-3 py-1 rounded-full text-xs font-semibold uppercase mb-3">
                  {categories.find(cat => cat.id === product.category)?.name}
                </span>
                <h3 className="text-xl font-bold text-white mb-2">{product.title}</h3>
                <p className="text-blue-100 mb-4 flex-1">{product.description}</p>
                <div className="flex flex-wrap gap-2 mb-4">
                  {product.features.map((feature, idx) => (
                    <span key={idx} className="bg-blue-800/40 text-blue-200 px-2 py-1 rounded-full text-xs border border-blue-700">{feature}</span>
                  ))}
                </div>
                <button
                  className="bg-green-500 text-white rounded-full px-5 py-2 font-semibold uppercase tracking-wide w-full transition hover:bg-green-600"
                  onClick={() => navigate(`/produit/${product.id}`)}
                >
                  En savoir plus
                </button>
              </div>
            </div>
          ))
        ) : (
          <div className="text-center py-16 text-blue-200 text-lg col-span-full">
            Aucun produit ne correspond à votre recherche.
          </div>
        )}
      </div>
    </div>
  );
};

export default Products;
