import React from 'react';

const Home = () => {
  return (
    <div className="relative min-h-screen w-full bg-cover bg-center flex items-center justify-center" style={{ backgroundImage: "url('https://static1.fr.squarespace.com/static/5134cbefe4b0c6fb04df8065/5dcb2d0669b294381f8ffb16/619e7e9834ba362249b859b0/1637777053765/RestaurantWebsite_Header_Desktop_1920x1062px.jpg')" }}>
      {/* Overlay */}
      <div className="absolute inset-0 bg-black/70 z-0" />
      {/* Content */}
      <div className="relative z-10 w-full max-w-4xl flex flex-col items-center justify-center min-h-[80vh] px-4">
        <div className="flex justify-center gap-16 flex-wrap mb-10">
          <div className="font-serif text-5xl md:text-7xl text-white font-light border-b-2 border-white px-6 pb-2 shadow-lg mb-2 tracking-widest text-center drop-shadow-lg">
            WATSON
          </div>
        </div>
        <div className="text-white text-lg md:text-xl text-center mb-8 drop-shadow-md">
          67 rue aziz bellal maarif, Casablanca, Maroc<br />
          info@watson.ma &nbsp;|&nbsp; +212 662-362121
        </div>
        <div className="bg-white/20 rounded-2xl p-8 md:p-12 w-full max-w-xl shadow-xl backdrop-blur-md">
          <h1 className="text-2xl md:text-3xl font-bold text-white mb-4 text-center drop-shadow">WATSON</h1>
          <p className="text-white text-base md:text-lg text-center">
            Watson est votre partenaire de confiance pour l'équipement professionnel de cuisine, restauration, boulangerie, et plus encore. Découvrez nos produits vedettes, nos réalisations, et contactez-nous pour toute demande personnalisée.
          </p>
        </div>
      </div>
    </div>
  );
};

export default Home;
