import React from 'react';
import { blogArticles } from '../data/blogData';
import { Link } from 'react-router-dom';
const BlogList = styled.div`
  display: flex;
  flex-direction: column;
  gap: 32px;
  padding: 40px 20px;
  max-width: 900px;
  margin: 0 auto;
`;

const BlogPost = styled.div`
  background: #fff;
  border-radius: 12px;
  box-shadow: 0 2px 12px rgba(0,0,0,0.08);
  display: flex;
  flex-direction: row;
  overflow: hidden;
`;

const Thumbnail = styled.img`
  width: 180px;
  height: 140px;
  object-fit: cover;
`;

const PostContent = styled.div`
  flex: 1;
  padding: 18px 24px;
`;

const Title = styled.h2`
  margin: 0 0 8px 0;
  font-size: 1.3em;
`;

const Excerpt = styled.p`
  margin: 0 0 12px 0;
  color: #444;
`;

const Date = styled.div`
  font-size: 0.95em;
  color: #888;
  margin-bottom: 8px;
`;

const ReadMore = styled(Link)`
  display: inline-block;
  background: #3498db;
  color: #fff;
  padding: 8px 18px;
  border-radius: 6px;
  text-decoration: none;
  font-weight: bold;
  margin-top: 8px;
  &:hover {
    background: #217dbb;
  }
`;
const Blog = () => (
  <div className="max-w-3xl mx-auto py-12 px-4 bg-gradient-to-br from-[#14213d] via-[#1a223f] to-[#14213d] min-h-screen">
    <h1 className="text-3xl md:text-4xl font-bold text-center mb-10 text-white">Blog</h1>
    <div className="flex flex-col gap-8">
      {blogArticles.map(article => (
        <div
          key={article.id}
          className="bg-[#1a223f] rounded-2xl shadow-lg border border-transparent hover:border-blue-400 hover:shadow-[0_0_20px_4px_rgba(59,130,246,0.5)] transition duration-300 overflow-hidden flex flex-col md:flex-row"
        >
          <img
            src={article.image}
            alt={article.title}
            className="w-full md:w-48 h-40 object-cover md:rounded-l-2xl md:rounded-r-none rounded-t-2xl md:rounded-t-none"
          />
          <div className="flex-1 p-6 flex flex-col justify-between">
            <div>
              <div className="text-sm text-blue-200 mb-2">{article.date} {article.author && <>| {article.author}</>}</div>
              <h2 className="text-xl font-semibold text-white mb-2">{article.title}</h2>
              <p className="text-blue-100 mb-4">{article.excerpt}</p>
            </div>
            <Link
              to={`/blog/${article.slug}`}
              className="inline-block bg-green-500 text-white px-5 py-2 rounded-full font-semibold text-sm transition hover:bg-green-600 w-max"
            >
              Lire la suite
            </Link>
          </div>
        </div>
      ))}
    </div>
  </div>
);

export default Blog;
