import React from 'react';
import { blogArticles } from '../data/blogData';

const Blog = () => (
  <div className="max-w-3xl mx-auto py-12 px-4">
    <h1 className="text-4xl font-bold mb-8 text-center text-gray-800">Blog</h1>
    <div className="grid gap-8">
      {blogArticles.map(article => (
        <div key={article.id} className="bg-white rounded-lg shadow p-6">
          <img src={article.image} alt={article.title} className="w-full h-56 object-cover rounded mb-4" />
          <h2 className="text-2xl font-semibold mb-2">{article.title}</h2>
          <div className="text-gray-500 text-sm mb-2">{article.date} â€” {article.author}</div>
          <p className="text-gray-700 mb-4">{article.excerpt}</p>
          {/* You can add a Link to a detail page here if you want */}
        </div>
      ))}
    </div>
  </div>
);

export default Blog;
