import React, { useState } from 'react';
import styled from 'styled-components';
import { products } from '../data/products';
import { useNavigate } from 'react-router-dom';

const Container = styled.div`
  max-width: 1200px;
  margin: 40px auto;
  padding: 0 20px;
`;

const Title = styled.h1`
  font-size: 2.5rem;
  font-weight: bold;
  color: #1a202c;
  margin-bottom: 32px;
  text-align: center;
`;

const Card = styled.div`
  background: #fff;
  border-radius: 16px;
  box-shadow: 0 4px 24px rgba(0,0,0,0.08);
  padding: 24px;
  margin-bottom: 24px;
  transition: transform 0.3s ease;
  &:hover {
    transform: translateY(-5px);
  }
`;

const CardTitle = styled.h2`
  font-size: 1.5rem;
  color: #2563eb;
  margin-bottom: 12px;
`;

const CardDescription = styled.p`
  color: #444;
  font-size: 1.08rem;
  line-height: 1.7;
`;

const Products = () => (
  <Container>
    <Title>Nos Produits</Title>
    <Card>
      <CardTitle>Grille-pain professionnel WATSON</CardTitle>
      <CardDescription>
        Notre grille-pain professionnel est conçu pour les établissements de restauration rapide. Il offre une performance exceptionnelle et une durabilité inégalée.
      </CardDescription>
    </Card>
    {/* Add more product cards here as needed */}
  </Container>
);

export default Products;
