import React, { useState } from 'react';
import styled from 'styled-components';
import { products } from '../data/products';

const ProductsContainer = styled.div`
  padding: 40px 20px;
  max-width: 1200px;
  margin: 0 auto;
`;

const PageTitle = styled.h1`
  text-align: center;
  color: #2c3e50;
  margin-bottom: 40px;
  font-size: 2.5em;
`;

const ProductGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
  gap: 30px;
  padding: 20px 0;
`;

const ProductCard = styled.div`
  background: white;
  border-radius: 10px;
  box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
  overflow: hidden;
  transition: transform 0.3s ease;
  
  &:hover {
    transform: translateY(-5px);
  }
`;

const ProductImage = styled.img`
  height: 250px;
  width: 100%;
  object-fit: cover;
  transition: transform 0.3s ease;

  ${ProductCard}:hover & {
    transform: scale(1.05);
  }
`;

const ProductInfo = styled.div`
  padding: 20px;
`;

const ProductTitle = styled.h3`
  color: #2c3e50;
  margin: 0 0 10px 0;
  font-size: 1.4em;
`;

const ProductDescription = styled.p`
  color: #666;
  line-height: 1.6;
  margin-bottom: 15px;
  font-size: 0.95em;
`;

const ProductCategory = styled.span`
  display: inline-block;
  background-color: #e8f4fd;
  color: #3498db;
  padding: 4px 8px;
  border-radius: 4px;
  font-size: 0.8em;
  margin-bottom: 15px;
`;

const ProductButton = styled.button`
  background-color: #3498db;
  color: white;
  border: none;
  padding: 10px 20px;
  border-radius: 5px;
  cursor: pointer;
  width: 100%;
  font-size: 1em;
  transition: background-color 0.3s ease;

  &:hover {
    background-color: #2980b9;
  }
`;

const SearchAndFilterContainer = styled.div`
  max-width: 1200px;
  margin: 0 auto 30px;
  padding: 20px;
  background: #f8f9fa;
  border-radius: 10px;
  display: flex;
  flex-wrap: wrap;
  gap: 20px;
  align-items: center;
`;

const SearchInput = styled.input`
  padding: 10px 15px;
  border: 1px solid #ddd;
  border-radius: 5px;
  font-size: 1em;
  flex: 1;
  min-width: 200px;
`;

const FilterSelect = styled.select`
  padding: 10px 15px;
  border: 1px solid #ddd;
  border-radius: 5px;
  font-size: 1em;
  background: white;
  cursor: pointer;
`;

const SortSelect = styled.select`
  padding: 10px 15px;
  border: 1px solid #ddd;
  border-radius: 5px;
  font-size: 1em;
  background: white;
  cursor: pointer;
`;

const FeaturedSection = styled.div`
  margin-bottom: 40px;
`;

const FeaturedTitle = styled.h2`
  text-align: center;
  color: #2c3e50;
  margin-bottom: 30px;
  font-size: 2em;
`;

const FeaturedGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
  gap: 30px;
  padding: 20px 0;
`;

const Products = () => {
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedCategory, setSelectedCategory] = useState('all');
  const [sortBy, setSortBy] = useState('name');

  const categories = [
    { id: 'all', name: 'Tous les produits' },
    { id: 'cuisine', name: 'Cuisine Professionnelle' },
    { id: 'restauration', name: 'Restauration Rapide' },
    { id: 'snacking', name: 'Snacking' },
    { id: 'hotellerie', name: 'Hôtellerie' },
    { id: 'boulangerie', name: 'Boulangerie' },
    { id: 'collective', name: 'Cuisine Collective' },
    { id: 'bar', name: 'Bar' },
    { id: 'stockage', name: 'Stockage' },
    { id: 'nettoyage', name: 'Nettoyage' },
    { id: 'specialise', name: 'Spécialisé' }
  ];

  const featuredProducts = products.slice(0, 3);

  const filteredProducts = products
    .filter(product => {
      const matchesSearch = product.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
                          product.description.toLowerCase().includes(searchTerm.toLowerCase());
      const matchesCategory = selectedCategory === 'all' || 
                            product.category === selectedCategory;
      return matchesSearch && matchesCategory;
    })
    .sort((a, b) => {
      if (sortBy === 'name') {
        return a.title.localeCompare(b.title);
      }
      return 0;
    });

  return (
    <ProductsContainer>
      <PageTitle>Nos Produits</PageTitle>
      
      <FeaturedSection>
        <FeaturedTitle>Produits Vedettes</FeaturedTitle>
        <FeaturedGrid>
          {featuredProducts.map(product => (
            <ProductCard key={product.id}>
              <ProductImage src={product.image} alt={product.title} />
              <ProductInfo>
                <ProductCategory>{categories.find(cat => cat.id === product.category)?.name}</ProductCategory>
                <ProductTitle>{product.title}</ProductTitle>
                <ProductDescription>{product.description}</ProductDescription>
                <ProductButton>En savoir plus</ProductButton>
              </ProductInfo>
            </ProductCard>
          ))}
        </FeaturedGrid>
      </FeaturedSection>

      <SearchAndFilterContainer>
        <SearchInput
          type="text"
          placeholder="Rechercher un produit..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
        <FilterSelect
          value={selectedCategory}
          onChange={(e) => setSelectedCategory(e.target.value)}
        >
          {categories.map(category => (
            <option key={category.id} value={category.id}>
              {category.name}
            </option>
          ))}
        </FilterSelect>
        <SortSelect
          value={sortBy}
          onChange={(e) => setSortBy(e.target.value)}
        >
          <option value="name">Trier par nom</option>
        </SortSelect>
      </SearchAndFilterContainer>

      <ProductGrid>
        {filteredProducts.map(product => (
          <ProductCard key={product.id}>
            <ProductImage src={product.image} alt={product.title} />
            <ProductInfo>
              <ProductCategory>{categories.find(cat => cat.id === product.category)?.name}</ProductCategory>
              <ProductTitle>{product.title}</ProductTitle>
              <ProductDescription>{product.description}</ProductDescription>
              <ProductButton>En savoir plus</ProductButton>
            </ProductInfo>
          </ProductCard>
        ))}
      </ProductGrid>
    </ProductsContainer>
  );
};

export default Products;
